import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page2({ inputValue2, setInputValue2 }) {
  const [counter, setCounter] = useState(2);
  const [question, setQuestion] = useState("How many Twitter accounts do you have?");
  const isInteger = (value) => Number.isInteger(Number(value)) && value !== '';

  useEffect(() => {
    const timer = setTimeout(() => setCounter(3), 500); // 500 ms sonra sayaç değişecek
    return () => clearTimeout(timer);
  }, [inputValue2]);

  return (
    <div className="App">
      <div className="logo-container">Leg1t</div>
      <h1 className="text-4xl mt-8 lg:text-[84px] font-extralight">
        Who are <span className="font-bold">you</span>
        <span className="font-extrabold text-primary">?</span>
      </h1>

      {/* Sayaç Animasyonu */}
      <div className={`counter ${counter === 2 ? 'counter-enter-active' : 'counter-exit-active'}`}>2/3</div>

      {/* Küçük resim */}
      <img src="/logo.png" className="image" alt="Logo" />

      {/* Soru Animasyonu */}
      <div className={`Question ${counter === 2 ? 'Question-enter-active' : 'Question-exit-active'}`}>
        {question}
      </div>

      {/* Input Kutusu */}
      <input
        className="page1-input"
        type="number"
        value={inputValue2}
        onChange={(e) => setInputValue2(e.target.value)}
        placeholder="Bir integer girin"
      />

      {/* Butonlar */}
      <div className="button-container">
        <Link to="/">
          <button className="previous-button">Önceki</button>
        </Link>
        <Link to={isInteger(inputValue2) ? "/hoşgeldiniz3" : "#"}>
          <button className="next-button" disabled={!isInteger(inputValue2)}>
            Sonraki
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Page2;
