import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';  // React Router bileşenlerini import ediyoruz

function App() {
  return (
    <Router>  {/* Sayfa yönlendirmesini sağlayacak Router bileşeni */}
      <div className="App">
        <div className="welcome-message">
          Hoşgeldiniz
        </div>
        {/* "Sonraki" butonunu ekliyoruz ve bu butona tıklandığında "Hoşgeldiniz2" sayfasına yönlendiriyoruz */}
        <Link to="/hoşgeldiniz2">
          <button className="next-button">Sonraki</button>
        </Link>
      </div>
      
      <Switch>
        {/* Ana sayfa */}
        <Route exact path="/">
          <div className="App">
            <div className="welcome-message">Hoşgeldiniz</div>
            <Link to="/hoşgeldiniz2">
              <button className="next-button">Sonraki</button>
            </Link>
          </div>
        </Route>

        {/* Yeni sayfa */}
        <Route path="/hoşgeldiniz2">
          <div className="App">
            <div className="welcome-message">Hoşgeldiniz 2</div>
          </div>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
