import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';

function App() {
  // Input değerini tutmak için state
  const [inputValue, setInputValue] = useState('');
  const [isValidInput, setIsValidInput] = useState(false);

  // Input değeri değiştiğinde, sadece integer olup olmadığını kontrol ediyoruz
  const handleInputChange = (event) => {
    const value = event.target.value;
    setInputValue(value);

    // Sadece integer kontrolü
    if (/^\d+$/.test(value)) {
      setIsValidInput(true);  // Geçerli bir integer
    } else {
      setIsValidInput(false);  // Geçersiz
    }
  };

  return (
    <Router>
      <div className="App">
        <Route>
          {/* 1. Sayfa */}
          <Route exact path="/">
            <div className="welcome-message">
              Hoşgeldiniz
            </div>
            {/* Input alanı ekliyoruz */}
            <input 
              type="text" 
              value={inputValue} 
              onChange={handleInputChange} 
              placeholder="Bir sayı girin" 
            />
            <br />
            {/* Sonraki butonunun durumu input değeri geçerli olduğunda aktif oluyor */}
            <Link to={isValidInput ? "/hoşgeldiniz2" : "#"}>
              <button className="next-button" disabled={!isValidInput}>
                Sonraki
              </button>
            </Link>
          </Route>

          {/* 2. Sayfa */}
          <Route path="/hoşgeldiniz2">
            <div className="welcome-message">
              Hoşgeldiniz 2
            </div>
            <Link to="/hoşgeldiniz3">
              <button className="next-button">Sonraki</button>
            </Link>
            <Link to="/">
              <button className="prev-button">Önceki</button>
            </Link>
          </Route>

          {/* 3. Sayfa */}
          <Route path="/hoşgeldiniz3">
            <div className="welcome-message">
              Hoşgeldiniz 3
            </div>
            <Link to="/hoşgeldiniz4">
              <button className="next-button">Sonraki</button>
            </Link>
            <Link to="/hoşgeldiniz2">
              <button className="prev-button">Önceki</button>
            </Link>
          </Route>

          {/* 4. Sayfa */}
          <Route path="/hoşgeldiniz4">
            <div className="welcome-message">
              Hoşgeldiniz 4
            </div>
            <Link to="/hoşgeldiniz5">
              <button className="next-button">Sonraki</button>
            </Link>
            <Link to="/hoşgeldiniz3">
              <button className="prev-button">Önceki</button>
            </Link>
          </Route>

          {/* 5. Sayfa */}
          <Route path="/hoşgeldiniz5">
            <div className="welcome-message">
              Hoşgeldiniz 5
            </div>
            <Link to="/hoşgeldiniz4">
              <button className="prev-button">Önceki</button>
            </Link>
          </Route>
        </Route>
      </div>
    </Router>
  );
}

export default App;
