import React from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';  // React Router bileşenlerini import ediyoruz

function App() {
  return (
    <Router>  {/* Sayfa yönlendirmesini sağlayacak Router bileşeni */}
      <div className="App">
        <Routes>
          {/* 1. Sayfa (Ana sayfa) */}
          <Route path="/" element={
            <div className="App">
              <div className="welcome-message">Hoşgeldiniz 1</div>
              <Link to="/hoşgeldiniz2">
                <button className="next-button">Sonraki</button>
              </Link>
            </div>
          } />

          {/* 2. Sayfa */}
          <Route path="/hoşgeldiniz2" element={
            <div className="App">
              <div className="welcome-message">Hoşgeldiniz 2</div>
              <Link to="/hoşgeldiniz3">
                <button className="next-button">Sonraki</button>
              </Link>
              <Link to="/">
                <button className="previous-button">Önceki</button>
              </Link>
            </div>
          } />

          {/* 3. Sayfa */}
          <Route path="/hoşgeldiniz3" element={
            <div className="App">
              <div className="welcome-message">Hoşgeldiniz 3</div>
              <Link to="/hoşgeldiniz4">
                <button className="next-button">Sonraki</button>
              </Link>
              <Link to="/hoşgeldiniz2">
                <button className="previous-button">Önceki</button>
              </Link>
            </div>
          } />

          {/* 4. Sayfa */}
          <Route path="/hoşgeldiniz4" element={
            <div className="App">
              <div className="welcome-message">Hoşgeldiniz 4</div>
              <Link to="/hoşgeldiniz5">
                <button className="next-button">Sonraki</button>
              </Link>
              <Link to="/hoşgeldiniz3">
                <button className="previous-button">Önceki</button>
              </Link>
            </div>
          } />

          {/* 5. Sayfa */}
          <Route path="/hoşgeldiniz5" element={
            <div className="App">
              <div className="welcome-message">Hoşgeldiniz 5</div>
              <Link to="/hoşgeldiniz4">
                <button className="previous-button">Önceki</button>
              </Link>
            </div>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
