import React, { useState, useEffect } from 'react';
import './App.css';

const Page5 = () => {
  const [inputValue, setInputValue] = useState('');
  const [isTimerActive, setIsTimerActive] = useState(false);
  const [counter, setCounter] = useState(0);
  const [message, setMessage] = useState('');
  const [inputSummary, setInputSummary] = useState({});

  useEffect(() => {
    let timer;
    if (isTimerActive && counter < 10) {
      timer = setInterval(() => setCounter((prev) => prev + 1), 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer);
  }, [isTimerActive, counter]);

  const handleSubmit = () => {
    setIsTimerActive(true);
    setMessage('Timer is running...');
  };

  const handleReset = () => {
    setIsTimerActive(false);
    setCounter(0);
    setMessage('');
    setInputValue('');
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSaveInput = () => {
    setInputSummary({
      ...inputSummary,
      inputValue: inputValue,
      timerStatus: isTimerActive ? 'Active' : 'Inactive',
      counter: counter,
    });
  };

  return (
    <div className="App">
      <div className="logo-container">
        <span className="logo-text">Logo</span>
      </div>

      <h1 className="heading">Page 5</h1>

      <div className="description-text">
        <p>Input and Timer Example</p>
      </div>

      <div className="input-summary">
        <div>
          <p className="input-title">Entered Value:</p>
          <p className="text-message">{inputSummary.inputValue || 'No input yet'}</p>
        </div>
        <div>
          <p className="input-title">Timer Status:</p>
          <p className="text-message">{inputSummary.timerStatus || 'Inactive'}</p>
        </div>
        <div>
          <p className="input-title">Timer Counter:</p>
          <p className="text-message">{inputSummary.counter || 0}</p>
        </div>
      </div>

      <div className="page1-input-container">
        <input
          className="page1-input"
          type="text"
          placeholder="Enter some text"
          value={inputValue}
          onChange={handleInputChange}
        />
      </div>

      <div className="button-container">
        <button className="next-button" onClick={handleSubmit}>
          Start Timer
        </button>
        <button className="previous-button" onClick={handleReset}>
          Reset
        </button>
      </div>

      <div className="counter">
        <p>{message}</p>
        <p>{counter}</p>
      </div>

      <div className="button-container">
        <button className="next-button" onClick={handleSaveInput}>
          Save Input
        </button>
      </div>
    </div>
  );
};

export default Page5;
