import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page1({ inputValue1, setInputValue1 }) {
  const [counter, setCounter] = useState(1);
  const [question, setQuestion] = useState("How many Matemask accounts do you have?");
  const isInteger = (value) => Number.isInteger(Number(value)) && value !== '';

  useEffect(() => {
    // Sayfa 1'deki animasyonları başlat
    const timer = setTimeout(() => setCounter(2), 500); // 500 ms sonra sayaç değişecek
    return () => clearTimeout(timer);
  }, [inputValue1]);

  return (
    <div className="App">
      <div className="logo-container">Leg1t</div>
      <h1 className="text-4xl mt-8 lg:text-[84px] font-extralight">
        Who are <span className="font-bold">you</span>
        <span className="font-extrabold text-primary">?</span>
      </h1>

      {/* Sayaç Animasyonu */}
      <div className={`counter ${counter === 1 ? 'counter-enter-active' : 'counter-exit-active'}`}>1/3</div>

      {/* Küçük resim */}
      <img src="/logo.png" className="image" alt="Logo" />

      {/* Soru Animasyonu */}
      <div className={`Question ${counter === 1 ? 'Question-enter-active' : 'Question-exit-active'}`}>
        {question}
      </div>

      {/* Input Kutusu */}
      <input
        className="page1-input"
        type="number"
        value={inputValue1}
        onChange={(e) => setInputValue1(e.target.value)}
        placeholder="Bir integer girin"
      />

      {/* Next Butonu */}
      <Link to={isInteger(inputValue1) ? "/hoşgeldiniz2" : "#"}>
        <button className="next-button" disabled={!isInteger(inputValue1)}>
          Sonraki
        </button>
      </Link>
    </div>
  );
}

export default Page1;
