import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page1({ inputValue1, setInputValue1 }) {
  const isInteger = (value) => Number.isInteger(Number(value)) && value !== '';

  // State to track the current question number
  const [questionNumber, setQuestionNumber] = useState(1);

  useEffect(() => {
    // Animation on page change
    setTimeout(() => {
      setQuestionNumber((prev) => prev + 1); // Increment the question number after the page loads
    }, 500);
  }, []);

  return (
    <div className="App">
      {/* Logo */}
      <div className="logo-container">
        Leg1t
      </div>

      {/* Başlık */}
      <h1 className="text-4xl mt-8 lg:text-[84px] font-extralight">
        Who are <span className="font-bold">you</span>
        <span className="font-extrabold text-primary">?</span>
      </h1>

      {/* Sayaç */}
      <div className="counter">
        {questionNumber}/3
      </div>

      {/* Küçük resim */}
      <img src="/logo.png" className="image" alt="Logo" />

      {/* Soru */}
      <div className="Question">
        how many {questionNumber === 1 ? 'Matemask' : questionNumber === 2 ? 'Twitter' : 'Telegram'} accounts do you have?
      </div>

      {/* Input Kutusu */}
      <input
        className="page1-input"
        type="number"
        value={inputValue1}
        onChange={(e) => setInputValue1(e.target.value)}
        placeholder="Bir integer girin"
      />

      {/* Next Butonu */}
      <Link to={isInteger(inputValue1) ? "/hoşgeldiniz2" : "#"}>
        <button className="next-button" disabled={!isInteger(inputValue1)}>
          Sonraki
        </button>
      </Link>
    </div>
  );
}

export default Page1;
