import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';  // React Router bileşenlerini import ediyoruz

function App() {
  return (
    <Router> {/* Sayfa yönlendirmesini sağlayacak Router bileşeni */}
      <div className="App">
        <Routes>
          {/* Ana sayfa */}
          <Route path="/" element={
            <div className="welcome-message">
              Hoşgeldiniz 1
              <Link to="/hoşgeldiniz2">
                <button className="next-button">Sonraki</button>
              </Link>
            </div>
          } />

          {/* Hoşgeldiniz2 sayfası */}
          <Route path="/hoşgeldiniz2" element={
            <div className="welcome-message">
              Hoşgeldiniz 2
              <Link to="/hoşgeldiniz3">
                <button className="next-button">Sonraki</button>
              </Link>
            </div>
          } />

          {/* Hoşgeldiniz3 sayfası */}
          <Route path="/hoşgeldiniz3" element={
            <div className="welcome-message">
              Hoşgeldiniz 3
              <Link to="/hoşgeldiniz4">
                <button className="next-button">Sonraki</button>
              </Link>
            </div>
          } />

          {/* Hoşgeldiniz4 sayfası */}
          <Route path="/hoşgeldiniz4" element={
            <div className="welcome-message">
              Hoşgeldiniz 4
            </div>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
