import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page4({ inputValue1, inputValue2, inputValue3, emailValue, setEmailValue }) {
  const isValidEmail = (email) => {
    const emailRegex = /^[a-zA-Z0-9._-]+@[a-zAZ0-9.-]+\.[a-zA-Z]{2,4}$/;
    return emailRegex.test(email);
  };

  return (
    <div className="App">
      {/* 1, 2, ve 3. sayfalardan alınan input değerlerini gösteriyoruz */}
      <div className="logo-container">
        <p className="logo">Logo</p>
      </div>

      <div className="input-summary">
        <p>1. Sayfa Input: {inputValue1}</p>
        <p>2. Sayfa Input: {inputValue2}</p>
        <p>3. Sayfa Input: {inputValue3}</p>
      </div>

      <h1 className="heading">UNIQUE</h1>
      <p className="description-text">Join the Waitlist. Earn points for Airdrop.</p>

      <input 
        type="email" 
        value={emailValue}
        onChange={(e) => setEmailValue(e.target.value)}
        placeholder="Bir email girin"
        className="page1-input"
      />
      
      <Link to={isValidEmail(emailValue) ? "/hoşgeldiniz5" : "#"}>
        <button className="next-button" disabled={!isValidEmail(emailValue)}>
          Sonraki
        </button>
      </Link>
    </div>
  );
}

export default Page4;
