import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page2({ inputValue2, setInputValue2 }) {
  const isInteger = (value) => Number.isInteger(Number(value)) && value !== '';

  // State to track the current question number
  const [questionNumber, setQuestionNumber] = useState(2);

  useEffect(() => {
    // Animation on page change
    setTimeout(() => {
      setQuestionNumber((prev) => prev + 1); // Increment the question number after the page loads
    }, 500);
  }, []);

  return (
    <div className="App">
      {/* Logo */}
      <div className="logo-container">
        Leg1t
      </div>

      {/* Başlık */}
      <h1 className="text-4xl mt-8 lg:text-[84px] font-extralight">
        Who are <span className="font-bold">you</span>
        <span className="font-extrabold text-primary">?</span>
      </h1>

      {/* Sayaç */}
      <div className="counter">
        {questionNumber}/3
      </div>

      {/* Küçük resim */}
      <img src="/logo.png" className="image" alt="Logo" />

      {/* Soru */}
      <div className="Question">
        how many {questionNumber === 1 ? 'Matemask' : questionNumber === 2 ? 'Twitter' : 'Telegram'} accounts do you have?
      </div>

      {/* Input Kutusu */}
      <input
        className="page1-input"
        type="number"
        value={inputValue2}
        onChange={(e) => setInputValue2(e.target.value)}
        placeholder="Bir integer girin"
      />

      {/* Butonlar */}
      <div className="button-container">
        <Link to="/">
          <button className="previous-button">Önceki</button>
        </Link>
        <Link to={isInteger(inputValue2) ? "/hoşgeldiniz3" : "#"}>
          <button className="next-button" disabled={!isInteger(inputValue2)}>
            Sonraki
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Page2;
