import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page3({ inputValue3, setInputValue3 }) {
  const isInteger = (value) => Number.isInteger(Number(value)) && value !== '';

  return (
    <div className="App">
      {/* Logo */}
      <div className="logo-container">
        Leg1t
      </div>

      {/* Başlık */}
      <h1 className="text-4xl mt-8 lg:text-[84px] font-extralight">
        Who are <span className="font-bold">you</span>
        <span className="font-extrabold text-primary">?</span>
      </h1>

      {/* Sayaç Animasyonu */}
      <div className="counter"><span className="other">3</span>/3</div>

      {/* Küçük resim */}
      <img src="/logo3.png" className="image" alt="Logo" />

      {/* Soru Animasyonu */}
      <div className="Question-new">How many Telegram accounts do you have?</div>

      {/* Input Kutusu */}
      <input
        className="page1-input"
        type="number"
        value={inputValue3}
        onChange={(e) => setInputValue3(e.target.value)}
        placeholder="Enter a number"
      />

      {/* Butonlar */}
      <div className="button-container">
        <Link to="/hoşgeldiniz2">
          <button className="previous-button">Back</button>
        </Link>
        <Link to={isInteger(inputValue3) ? "/hoşgeldiniz4" : "#"}>
          <button className="next-button" disabled={!isInteger(inputValue3)}>
            Next
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Page3;
