import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page3({ inputValue3, setInputValue3 }) {
  const isInteger = (value) => Number.isInteger(Number(value)) && value !== '';

  const [counter, setCounter] = useState(3); // Sayaç değeri
  const [question, setQuestion] = useState("how many Telegram accounts do you have?"); // Soru metni

  // Sayaç değerini güncelleme
  useEffect(() => {
    const interval = setInterval(() => {
      setCounter((prev) => prev < 3 ? prev + 1 : 1);
    }, 3000); // 3 saniyede bir sayaç değeri değişiyor
    return () => clearInterval(interval); // Temizleme işlemi
  }, []);

  // Soru metnini değiştirme
  useEffect(() => {
    const questions = [
      "how many Matemask accounts do you have?",
      "how many Twitter accounts do you have?",
      "how many Telegram accounts do you have?"
    ];
    setQuestion(questions[counter - 1]); // Soru metni sayaç değerine göre güncellenir
  }, [counter]);

  return (
    <div className="App">
      {/* Logo */}
      <div className="logo-container">
        Leg1t
      </div>

      {/* Başlık */}
      <h1 className="text-4xl mt-8 lg:text-[84px] font-extralight">
        Who are <span className="font-bold">you</span>
        <span className="font-extrabold text-primary">?</span>
      </h1>

      {/* Sayaç */}
      <div className="counter">{counter}/3</div>

      {/* Küçük resim */}
      <img src="/logo.png" className="image" alt="Logo" />

      {/* Soru */}
      <div className="Question">{question}</div>

      {/* Input Kutusu */}
      <input
        className="page1-input"
        type="number"
        value={inputValue3}
        onChange={(e) => setInputValue3(e.target.value)}
        placeholder="Bir integer girin"
      />

      {/* Butonlar */}
      <div className="button-container">
        <Link to="/hoşgeldiniz2">
          <button className="previous-button">Önceki</button>
        </Link>
        <Link to={isInteger(inputValue3) ? "/sonuc" : "#"}>
          <button className="next-button" disabled={!isInteger(inputValue3)}>
            Sonraki
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Page3;
