import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page1({ inputValue1, setInputValue1 }) {
  const isInteger = (value) => Number.isInteger(Number(value)) && value !== '';

  return (
    <div className="App">
      {/* Logo */}
      <div className="logo-container">
        Leg1t
      </div>

      {/* Başlık */}
      <h1 className="text-4xl">
        Who are <span className="font-bold">you</span>
        <span className="font-extrabold text-primary">?</span>
      </h1>

      {/* Sayaç Animasyonu */}
      <div className="counter"><span className="other">1</span>/3</div>

      {/* Küçük resim */}
      <img src="/logo.png" className="image" alt="Logo" />

      {/* Yeni Tasarım Soru Metni */}
<div className="question-container">
  how many
  <p  style={{ margin: '0px 4px', display: 'inline-block' }}>
    
    <span className="absolute" style={{ opacity: 1, transform: 'none', whiteSpace: 'nowrap' }}>Metamask</span>
  </p>
  accounts do you have
  <span className="question-symbol">?</span>
</div>

      {/* Input Kutusu */}
      <input 
        className="page1-input"
        type="number"
        value={inputValue1}
        onChange={(e) => setInputValue1(e.target.value)} 
        placeholder="Enter a number"
      />

      {/* Next Butonu */}
      <Link to={isInteger(inputValue1) ? "/hoşgeldiniz2" : "#"}>
        <button className="next-button" disabled={!isInteger(inputValue1)}>
          Next
        </button>
      </Link>
    </div>
  );
}

export default Page1;
