import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Page1({ inputValue1, setInputValue1 }) {
  const isInteger = (value) => Number.isInteger(Number(value)) && value !== '';

  return (
    <div className="App">
      {/* Çizgiler */}
     

      {/* Logo */}
      <div className="logo-container">
        LOGO
      </div>

      {/* Başlık */}
      <div className="heading">WHO ARE YOU?</div>
      <div className="Question">how many Matemask accounts do you have? </div>
      {/* Sayaç */}
      <div className="counter">1/3</div>

      {/* Küçük resim (örneğin bir logo) */}
      <img src="src/logo.png" className="image" />

      {/* Input Kutusu */}
      <input 
        className="page1-input"
        type="number"
        value={inputValue1}
        onChange={(e) => setInputValue1(e.target.value)} 
        placeholder="Bir integer girin"
      />

      {/* Next Butonu */}
      <Link to={isInteger(inputValue1) ? "/hoşgeldiniz2" : "#"}>
        <button className="next-button" disabled={!isInteger(inputValue1)}>
          Sonraki
        </button>
      </Link>
    </div>
  );
}

export default Page1;
