import React from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';  // React Router bileşenlerini import ediyoruz

// Hoşgeldiniz sayfası bileşeni
function WelcomePage() {
  return (
    <div className="App">
      <div className="welcome-message">
        Hoşgeldiniz
      </div>
      {/* "Sonraki" butonunu ekliyoruz ve bu butona tıklandığında "Hoşgeldiniz2" sayfasına yönlendiriyoruz */}
      <Link to="/hoşgeldiniz2">
        <button className="next-button">Sonraki</button>
      </Link>
    </div>
  );
}

// Hoşgeldiniz 2 sayfası bileşeni
function WelcomePage2() {
  return (
    <div className="App">
      <div className="welcome-message">
        Hoşgeldiniz 2
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>  {/* Sayfa yönlendirmesini sağlayacak Router bileşeni */}
      <Routes>  {/* Routes, yönlendirmeyi yapacağımız yer */}
        {/* Ana sayfa */}
        <Route path="/" element={<WelcomePage />} />
        
        {/* Hoşgeldiniz 2 sayfası */}
        <Route path="/hoşgeldiniz2" element={<WelcomePage2 />} />
      </Routes>
    </Router>
  );
}

export default App;
